  $: getRoute = (item: number, path: string) => {
    let host = "";
    switch (item) {
      case 0:
        host = "https://starter.vue2.saasfrontends.com";
        break;
      case 1:
        host = "https://starter.vue3.saasfrontends.com";
        break;
      case 2:
        host = "https://starter.react.saasfrontends.com";
        break;
      case 3:
        host = "https://starter.svelte.saasfrontends.com";
        break;
    }

    if (path !== undefined) {
      host += path;
    }
    return host;
  };
  $: updateRoute = (value: number) => {
    window.location.replace(getRoute(value, $url.pathname));
  };
  $: getRouteInVue2 = () => {
    return getRoute(0, $url.pathname);
  };
  $: getRouteInVue3 = () => {
    return getRoute(1, $url.pathname);
  };
